@tailwind base;
@tailwind components;
@tailwind utilities;

@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+SC:wght@400;500;600;700;900&display=swap');

:root {
  --background: 30 20% 98%;
  --foreground: 30 10% 15%;

  --card: 0 0% 100%;
  --card-foreground: 30 10% 15%;

  --popover: 0 0% 100%;
  --popover-foreground: 30 10% 15%;

  --primary: 24 95% 53%;
  --primary-foreground: 0 0% 100%;

  --secondary: 30 15% 95%;
  --secondary-foreground: 30 10% 30%;

  --muted: 30 10% 92%;
  --muted-foreground: 30 5% 45%;

  --accent: 30 15% 92%;
  --accent-foreground: 30 10% 15%;

  --destructive: 0 84% 60%;
  --destructive-foreground: 0 0% 100%;

  --border: 30 15% 90%;
  --input: 30 15% 90%;
  --ring: 24 95% 53%;

  --radius: 1rem;

  /* Custom colors for cards */
