@tailwind base;
@tailwind components;
@tailwind utilities;

@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+SC:wght@400;500;600;700;900&display=swap');

@layer base {
  :root {
    --background: 30 20% 98%;
    --foreground: 30 10% 20%;

    --card: 0 0% 100%;
    --card-foreground: 30 10% 20%;

    --popover: 0 0% 100%;
    --popover-foreground: 30 10% 20%;

    --primary: 24 90% 55%;
    --primary-foreground: 0 0% 100%;

    --secondary: 30 15% 95%;
    --secondary-foreground: 30 10% 30%;

    --muted: 30 10% 92%;
    --muted-foreground: 30 5% 45%;

    --accent: 30 15% 92%;
    --accent-foreground: 30 10% 20%;

    --destructive: 0 84% 60%;
    --destructive-foreground: 0 0% 100%;

    --border: 30 15% 88%;
    --input: 30 15% 90%;
    --ring: 24 90% 55%;

    --radius: 1rem;

    /* Custom card colors */
    --card-blue: 220 70% 92%;
    --card-blue-dark: 220 50% 35%;
    --card-purple: 265 50% 92%;
    --card-purple-dark: 265 40% 35%;
    --card-pink: 350 60% 93%;
    --card-pink-dark: 350 50% 35%;
    --card-green: 145 50% 90%;
    --card-green-dark: 145 40% 32%;

    /* Laurel gold */
    --laurel: 38 60% 45%;
  }

  .dark {
    --background: 30 10% 10%;
    --foreground: 30 10% 90%;
  }
}

* {
  font-family: 'Noto Sans SC', system-ui, -apple-system, sans-serif;
}

body {
  background-color: hsl(var(--background));
  color: hsl(var(--foreground));
}

@layer utilities {
  .text-balance {
    text-wrap: balance;
  }
}

